CREATE OR REPLACE FUNCTION obter_desc_microrregiao (nr_seq_microrregiao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_micro_reg_w varchar(60);


BEGIN
	SELECT max(DS_MICROREGIAO)
	INTO STRICT ds_micro_reg_w
	FROM microregiao
	WHERE nr_sequencia = nr_seq_microrregiao_p;

