CREATE OR REPLACE FUNCTION obter_desc_moeda_ordem (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_moeda_w	varchar(100);


BEGIN 
 
if (nr_ordem_compra_p IS NOT NULL AND nr_ordem_compra_p::text <> '') then 
	select	b.ds_moeda 
	into STRICT	ds_moeda_w 
	from	moeda b, 
		ordem_compra a 
	where 	a.cd_moeda		= b.cd_moeda 
	and	a.nr_ordem_compra	= nr_ordem_compra_p;
end if;
 
return ds_moeda_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
