CREATE OR REPLACE FUNCTION obter_desc_morfologia ( cd_morfologia_p text, nr_seq_morf_desc_adic_p bigint default null) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

BEGIN
	if (cd_morfologia_p IS NOT NULL AND cd_morfologia_p::text <> '') then
		select coalesce((select a.ds_morfologia from CIDO_MORFOLOGIA_DESC_ADIC a
		where a.nr_sequencia = nr_seq_morf_desc_adic_p and a.cd_morfologia = cd_morfologia_p),
		b.ds_morfologia)
		into STRICT
		ds_retorno_w
		from
		CIDO_MORFOLOGIA b
		where cd_morfologia = cd_morfologia_p;
	end if;
	return ds_retorno_w;
end;
$body$
