CREATE OR REPLACE FUNCTION obter_desc_motivo_afast_cad ( nr_seq_motivo_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ie_cadastro_w			varchar(1);


BEGIN

select 	substr(coalesce(obter_valor_param_usuario(3009, 15, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_p),'D'),1,1)
into STRICT	ie_cadastro_w
;

if (ie_cadastro_w = 'C') then
	select 	max(ds_motivo_afastamento)
	into STRICT	ds_retorno_w
	from 	motivo_afast_escala
	where	nr_sequencia = nr_seq_motivo_p;
else
	select 	substr(obter_valor_dominio(3661,nr_seq_motivo_p),1,255)
	into STRICT	ds_retorno_w
	;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
