CREATE OR REPLACE FUNCTION obter_desc_motivo_afast (nr_seq_motivo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_afastamento_w	varchar(255);



BEGIN

ds_motivo_afastamento_w	:= null;

if (coalesce(nr_seq_motivo_p,0) > 0) then

	select 	max(ds_motivo_afastamento)
	into STRICT	ds_motivo_afastamento_w
	from	motivo_afast_escala
	where	nr_sequencia = nr_seq_motivo_p;

end if;

return	ds_motivo_afastamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
