CREATE OR REPLACE FUNCTION obter_desc_motivo_auditoria (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_auditoria_w	varchar(80);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	select	ds_motivo_auditoria
	into STRICT	ds_motivo_auditoria_w
	from	auditoria_motivo
	where	nr_sequencia = nr_sequencia_p;
end if;

return	ds_motivo_auditoria_w;

end;
$body$
LANGUAGE PLPGSQL
