CREATE OR REPLACE FUNCTION obter_desc_motivo_baixa (cd_motivo_baixa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_w		varchar(50);


BEGIN

select	ds_tipo_baixa
into STRICT	ds_motivo_w
from	tipo_baixa_prescricao
