CREATE OR REPLACE FUNCTION obter_desc_motivo_coleta (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_motivo_coleta_w	varchar(255);
ds_retorno_w	varchar(255);


BEGIN 
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	select	a.ds_motivo_coleta 
	into STRICT	ds_motivo_coleta_w 
	from	biob_motivo_coleta a 
	where	a.nr_sequencia = nr_sequencia_p;
end if;
 
if (ds_motivo_coleta_w IS NOT NULL AND ds_motivo_coleta_w::text <> '') then 
	ds_retorno_w := ds_motivo_coleta_w;
else 
	ds_retorno_w := '';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
