CREATE OR REPLACE FUNCTION obter_desc_motivo_desconto_orc ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_desconto_w motivo_desconto_orcamento.ds_motivo_desconto%type;


BEGIN

select 	max(a.ds_motivo_desconto)
into STRICT 	ds_motivo_desconto_w
