CREATE OR REPLACE FUNCTION obter_desc_motiv_ocorr_escrit ( nr_seq_ocorr_motivo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (nr_seq_ocorr_motivo_p IS NOT NULL AND nr_seq_ocorr_motivo_p::text <> '') then
	select	substr(max(b.ds_motivo),1,255) ds
	into STRICT	ds_retorno_w
	from	banco_ocorr_motivo_ret b
	where	b.nr_sequencia 		= nr_seq_ocorr_motivo_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
