CREATE OR REPLACE FUNCTION obter_desc_mot_estorno_alta ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_w	varchar(255);


BEGIN
if (coalesce(nr_sequencia_p,0) > 0) then

	select 	max(ds_motivo_estorno_alta)
	into STRICT	ds_motivo_w
	from	motivo_estorno_alta
	where	nr_sequencia  = nr_sequencia_p;

end if;

return	ds_motivo_w;

end;
$body$
