CREATE OR REPLACE FUNCTION obter_desc_mot_inter ( CD_MOTIVO_P bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(300);


BEGIN
if (CD_MOTIVO_P IS NOT NULL AND CD_MOTIVO_P::text <> '') then
	begin
	select	ds_motivo
	into STRICT	ds_retorno_w
	from	motivo_interdicao_leito
	where	cd_motivo	= CD_MOTIVO_P;
	end;
end if;

RETURN ds_retorno_w;
END;
