CREATE OR REPLACE FUNCTION obter_desc_mot_manut ( cd_motivo_manut_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
if (cd_motivo_manut_p IS NOT NULL AND cd_motivo_manut_p::text <> '') then
	begin
	select	ds_motivo_manutencao
	into STRICT	ds_retorno_w
	from	tipo_manutencao
	where	nr_sequencia	= cd_motivo_manut_p;
	end;
end if;

return ds_retorno_w;
end;
$body$
