SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_muni_cep (cd_cep_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(240)	:= '';
ie_cep_novo_w		varchar(01)	:= '';
ie_cep_logradouro_w	varchar(01)	:= 'N';
ie_forma_consistir_w	varchar(5)	:= 'N';

c01 CURSOR FOR
	SELECT	a.nm_localidade
	from	cep_loc a
	where	a.cd_cep	= cd_cep_p
	  and	coalesce(a.nr_seq_superior, 0) = 0
	  and	ie_cep_novo_w	= 'S'
	
union

	SELECT	b.nm_localidade
	from	cep_localidade b
	where	b.cd_localidade	= cd_cep_p
	and	ie_cep_novo_w	= 'N';

c02 CURSOR FOR
	SELECT  c.ds_municipio
	from	sus_municipio c
	where	cd_municipio_ibge = cd_cep_p;


BEGIN

if (coalesce(cd_cep_p::text, '') = '') then
	return null;
end if;


begin
ie_forma_consistir_w := Obter_Param_Usuario(916, 86, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_forma_consistir_w);

select	coalesce(vl_parametro, vl_parametro_padrao)
into STRICT	ie_cep_novo_w
from	funcao_parametro
where	cd_funcao	= 0
and	nr_sequencia	= 25;


begin
if (cd_cep_p IS NOT NULL AND cd_cep_p::text <> '') and (ie_forma_consistir_w = 'S') then
	begin
	open c01;
	loop
	EXIT WHEN NOT FOUND; /* apply on c01 */
		fetch c01 into ds_resultado_w;
	end loop;
	close c01;
	end;
end if;
exception
	when others then
	ds_resultado_w	:= '';
end;

if (cd_cep_p IS NOT NULL AND cd_cep_p::text <> '') and (ie_forma_consistir_w = 'M') then
	begin
	open c02;
	loop
	EXIT WHEN NOT FOUND; /* apply on c02 */
		fetch c02 into ds_resultado_w;
	end loop;
	close c02;
	end;
end if;
exception
	when others then
	ds_resultado_w	:= '';
end;


if (coalesce(ds_resultado_w::text, '') = '') or (ds_resultado_w = '') then
	begin

	if (ie_cep_novo_w		= 'S') then
		select	max(b.nm_localidade)
		into STRICT	ds_resultado_w
		from	cep_loc b,
			cep_log a
		where	(a.nr_seq_loc)::numeric 	= b.nr_sequencia
		and	a.cd_cep	= cd_cep_p;
		if (coalesce(ds_resultado_w::text, '') = '') then
			select	max(b.nm_localidade)
			into STRICT	ds_resultado_w
			from	cep_loc b
			where	b.cd_cep	= cd_cep_p;
		end if;
	else
		select	max(nm_localidade)
		into STRICT	ds_resultado_w
		from	cep_logradouro
		where	cd_logradouro	= cd_cep_p;
	end if;

	if (ie_forma_consistir_w = 'M') then
		select  max(c.ds_municipio)
		into STRICT	ds_resultado_w
		from	sus_municipio c
		where	cd_municipio_ibge = cd_cep_p;
	end if;


	exception
		when others then
		ds_resultado_w	:= '';
	end;
end if;


RETURN ds_resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_desc_muni_cep (cd_cep_p text) FROM PUBLIC;

