SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_natureza_oper_cfop ( cd_natureza_operacao_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_estabelecimento_w	bigint;					
ds_retorno_w		varchar(255);
cd_cfop_w		varchar(255);
ds_natureza_w		varchar(255);
ie_mostra_cfop_nf_w	varchar(1) := 'N';

 

BEGIN 
 
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
 
if (cd_natureza_operacao_p IS NOT NULL AND cd_natureza_operacao_p::text <> '') then 
	begin 
	select	ds_natureza_operacao, 
		cd_cfop 
	into STRICT	ds_natureza_w, 
		cd_cfop_w 
	from	natureza_operacao 
	where	cd_natureza_operacao = cd_natureza_operacao_p;
	 
	if (cd_estabelecimento_w > 0) then 
	 
		select	coalesce(max(ie_mostra_cfop_nf),'N') 
		into STRICT	ie_mostra_cfop_nf_w 
		from	parametro_compras 
		where	cd_estabelecimento = cd_estabelecimento_w;
		 
	end if;
	 
	if (ie_mostra_cfop_nf_w = 'S') and (cd_cfop_w IS NOT NULL AND cd_cfop_w::text <> '') then	 
		ds_retorno_w := substr('(' || cd_cfop_w || ') - ' || ds_natureza_w,1,255);
	else 
		ds_retorno_w := substr(ds_natureza_w,1,255);
	end if;	
	end;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
