CREATE OR REPLACE FUNCTION obter_desc_nat_operacao_rps ( cd_estabelecimento_p bigint, ie_nat_operacao_p bigint) RETURNS varchar AS $body$
DECLARE

 
/* 
   1 - NATUREZA OPERACAO RPS 
   2- Nome natureza operacao RPS 
*/
 
 
 
ds_retorno_w			varchar(255);
ie_nat_operacao_w		integer;


BEGIN 
 
select	coalesce(max(ie_nat_oper_rps),0) 
into STRICT	ie_nat_operacao_w 
from	parametro_compras 
where	cd_estabelecimento = cd_Estabelecimento_p 
and	ie_nat_oper_rps	= ie_nat_operacao_p;
 
select	substr(b.ds_valor_dominio,1,50) 
into STRICT	ds_retorno_w 
from	valor_dominio b, 
	dominio a 
where	a.cd_dominio	= 3358 
and	a.cd_dominio	= b.cd_dominio 
and	b.vl_dominio	= ie_nat_operacao_w;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
