CREATE OR REPLACE FUNCTION obter_desc_nivel_valor (nr_seq_nivel_valor_p man_nivel_valor.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_nivel_valor_w				dic_expressao.ds_expressao_br%type;


BEGIN

select	obter_desc_expressao(a.cd_exp_nivel)
into STRICT	ds_nivel_valor_w
from	man_nivel_valor a
where	nr_sequencia = nr_seq_nivel_valor_p;

if (ds_nivel_valor_w = '' or coalesce(ds_nivel_valor_w::text, '') = '') then

	select	ds_nivel
	into STRICT	ds_nivel_valor_w
	from	man_nivel_valor a
	where	nr_sequencia = nr_seq_nivel_valor_p;

end if;

return	ds_nivel_valor_w;

end;
$body$
