CREATE OR REPLACE FUNCTION obter_desc_obj_schematic (nr_Seq_objeto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_objeto_w	varchar(255);


BEGIN

begin
select 	substr(CASE WHEN ie_tipo_componente='WDLG' THEN ds_objeto  ELSE obter_desc_expressao(cd_exp_desc_obj,ds_objeto) END ,1,250)
into STRICT	ds_objeto_w
from 	objeto_schematic
where 	nr_sequencia = nr_Seq_objeto_p;
exception
when others then
	ds_objeto_w	:= null;
end;

return ds_objeto_w;


end;
$body$
