CREATE OR REPLACE FUNCTION obter_desc_operacao_terceiro ( nr_seq_operacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (nr_seq_operacao_p IS NOT NULL AND nr_seq_operacao_p::text <> '') then
	select 	substr(ds_operacao,1,255)
	into STRICT	ds_retorno_w
	from 	operacao_terceiro
	where 	nr_sequencia = nr_seq_operacao_p;
end if;

RETURN ds_retorno_w;

END;
