SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_orcamento_compras ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*ie_opcao_p
D - Descrição
C - Código fonte
CD - Código + descrição*/
ds_orcamento_w	varchar(255);
cd_orcamento_w	varchar(255);
ds_resultado_w	varchar(255);


BEGIN

select	ds_orcamento,
	cd_orcamento
into STRICT	ds_orcamento_w,
	cd_orcamento_w
from	orcamento_compra
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'D') then
	ds_resultado_w := ds_orcamento_w;
elsif (ie_opcao_p = 'C') then
	ds_resultado_w := cd_orcamento_w;
elsif (ie_opcao_p = 'CD') then
	ds_resultado_w := cd_orcamento_w || ' - ' || ds_orcamento_w;
end if;


return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
