SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_ordem_compra_tit (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


pr_desc_financeiro_w	double precision;
vl_saldo_titulo_w		double precision;
vl_desconto_w		double precision;


BEGIN

select	coalesce(max(c.vl_saldo_titulo),0),
	coalesce(max(a.pr_desc_financeiro),0)
into STRICT	vl_saldo_titulo_w,
	pr_desc_financeiro_w
from	ordem_compra a,
	nota_fiscal b,
	titulo_pagar c
where	a.nr_ordem_compra	= b.nr_ordem_compra
and	b.nr_sequencia	= c.nr_seq_nota_fiscal
and	c.nr_titulo		= nr_titulo_p;

vl_desconto_w	:= dividir_sem_round((vl_saldo_titulo_w * pr_desc_financeiro_w)::numeric,100);

return	vl_desconto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
