SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_orientacao_checkup (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p: T Titulo  -  D Descrição */

ds_orientacao_w	varchar(2000);
ds_titulo_w	varchar(80);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	ds_titulo,
		ds_orientacao
	into STRICT	ds_titulo_w,
		ds_orientacao_w
	from	checkup_tipo_orientacao
	where	nr_sequencia	= nr_sequencia_p;
end if;

if (ie_opcao_p = 'T') then
	return	ds_titulo_w;
else
	return	ds_orientacao_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
