CREATE OR REPLACE FUNCTION obter_desc_pacote_acomod (nr_seq_pacote_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_sequencia_w		bigint;


BEGIN

/*NÃ£o informado*/

ds_retorno_w:= wheb_mensagem_pck.get_texto(300666);

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	pacote_tipo_acomodacao
where	nr_seq_pacote = nr_seq_pacote_p;

if (coalesce(nr_sequencia_w,0) > 0) then
	select	substr(obter_descricao_procedimento(cd_procedimento,ie_origem_proced),1,255)
	into STRICT	ds_retorno_w
	from	pacote_tipo_acomodacao
	where	nr_seq_pacote = nr_seq_pacote_p
	and 	nr_sequencia = nr_sequencia_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
