CREATE OR REPLACE FUNCTION obter_desc_pacote (nr_seq_pacote_p bigint) RETURNS varchar AS $body$
DECLARE

ds_pacote_w	varchar(240);

BEGIN

if (nr_seq_pacote_p IS NOT NULL AND nr_seq_pacote_p::text <> '') then
	select	substr(Obter_Descricao_Procedimento(CD_PROCED_PACOTE,ie_origem_proced),1,240)
	into STRICT	ds_pacote_w
	from	pacote
	where	nr_seq_pacote = nr_seq_pacote_p;
end if;
return	ds_pacote_w;

end;
$body$
