CREATE OR REPLACE FUNCTION obter_desc_pac_pend_esp (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_pendencia_w	varchar(255);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	select	substr(ds_pendencia,1,255)
	into STRICT	ds_pendencia_w
	from	pendencia_paciente_espera
	where	nr_sequencia = nr_sequencia_p;
end if;

return	ds_pendencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
