CREATE OR REPLACE FUNCTION obter_desc_pastas_pep (nr_seq_item_p bigint, nr_seq_dic_obj_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select	max(ds_caption)
into STRICT	ds_retorno_w
from (
		SELECT 	a.ds_caption_instituicao ds_caption
		from	prontuario_item_pasta a,
				prontuario_pasta b
		where	b.nr_sequencia = a.nr_seq_pasta
		and		(a.ds_caption_instituicao IS NOT NULL AND a.ds_caption_instituicao::text <> '')
		and		a.nr_seq_item = nr_seq_item_p
		and		b.nr_seq_dic_obj = nr_seq_dic_obj_p
		and	not 	exists (	SELECT	1
								from	perfil_item_pront x,
										perfil_item_pront_pasta y
								where	x.nr_sequencia = y.nr_seq_item_perfil
								and		y.nr_seq_item_pasta = a.nr_sequencia
								and		x.cd_perfil = obter_perfil_ativo
								and		x.nr_seq_item_pront = nr_seq_item_p
								and		(y.ds_pasta_instituicao IS NOT NULL AND y.ds_pasta_instituicao::text <> ''))
		
union all

		select 	c.ds_pasta_instituicao ds_caption
		from	perfil_item_pront d,
				perfil_item_pront_pasta c,
				prontuario_item_pasta b,
				prontuario_pasta a
		where	a.nr_sequencia = b.nr_seq_pasta
		and		b.nr_sequencia = c.nr_seq_item_pasta
		and		d.nr_sequencia = c.nr_seq_item_perfil
		and		d.cd_perfil = obter_perfil_ativo
		and		d.nr_seq_item_pront = nr_seq_item_p
		and		a.ie_situacao = 'A'
		and		(c.ds_pasta_instituicao IS NOT NULL AND c.ds_pasta_instituicao::text <> '')
		and		a.nr_seq_dic_obj = nr_seq_dic_obj_p) alias5;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
