CREATE OR REPLACE FUNCTION obter_desc_pasta_concat_sup (nr_seq_pasta_p bigint, nr_seq_funcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255) := '';

ds_pasta_w varchar(4000);
nr_seq_obj_sup_w dic_objeto.nr_seq_obj_sup%Type;


BEGIN

select	max(coalesce(substr(obter_desc_expressao(cd_exp_texto),1,254),'')) ds_exp_texto,
	max(coalesce(nr_seq_obj_sup ,0))
into STRICT	ds_pasta_w,
	nr_seq_obj_sup_w
from	dic_objeto
where	cd_funcao = nr_seq_funcao_p
and	ie_tipo_objeto = 'P'
and	nr_sequencia = nr_seq_pasta_p;

ds_retorno_w := ds_pasta_w;

if ((nr_seq_obj_sup_w IS NOT NULL AND nr_seq_obj_sup_w::text <> '') and nr_seq_obj_sup_w <> '0') then
	ds_retorno_w := obter_desc_pasta_concat_sup(nr_seq_obj_sup_w, nr_seq_funcao_p) || ' - ' || ds_retorno_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
