CREATE OR REPLACE FUNCTION obter_desc_pasta_item_pepo ( nr_seq_item_pasta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
if (nr_seq_item_pasta_p IS NOT NULL AND nr_seq_item_pasta_p::text <> '') then
	select	substr(obter_desc_expressao(a.cd_exp_desc_pasta,a.ds_caption),1,255) ds_caption
	into STRICT	ds_retorno_w
	from	prontuario_item_pasta b,
		prontuario_pasta a
	where	a.nr_sequencia = b.nr_seq_pasta_pepo
	and	b.nr_sequencia = nr_seq_item_pasta_p;
end if;

return ds_retorno_w;
end;
$body$
