CREATE OR REPLACE FUNCTION obter_desc_pat_operacao (nr_seq_operacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		pat_operacao.ds_operacao%type;


BEGIN

begin
	select	coalesce(obter_desc_expressao(cd_exp_operacao),a.ds_operacao)
	into STRICT	ds_retorno_w
	from	pat_operacao a
	where	a.nr_sequencia = nr_seq_operacao_p;
exception
when no_data_found then
	ds_retorno_w := '';

when too_many_rows then raise;
   
end;

return	ds_retorno_w;

end;
$body$
