CREATE OR REPLACE FUNCTION obter_desc_peca_principal ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
	select coalesce(max(a.ds_amostra),'')
	into STRICT   ds_retorno_w
	from   tipo_amostra_patologia a,
	       prescr_proc_peca b
	where  a.nr_sequencia = b.nr_seq_amostra_princ
	and    b.nr_prescricao = nr_prescricao_p
	and    b.nr_seq_prescr  = nr_seq_prescr_p
	and    b.ie_peca_principal = 'S';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
