SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_pendencia_guia (ds_pendencia_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w			varchar(4000);
ds_pendencia_w			varchar(255);
cd_inconsistencia_w		varchar(10);
ds_inconsistencia_w		varchar(60);

BEGIN

ds_pendencia_w		:= replace((ds_pendencia_p || '-1'), ' ', ',');


for i in 1.. length(ds_pendencia_w) LOOP
	begin
	if (substr(ds_pendencia_w,i,1) = ',') then
		begin

		select	ds_inconsistencia
		into STRICT	ds_inconsistencia_w
		from	inconsistencia
		where	cd_inconsistencia = cd_inconsistencia_w;

		ds_retorno_w		:= ds_retorno_w || ds_inconsistencia_w || chr(13);
		cd_inconsistencia_w	:= '';

		end;
	else
		cd_inconsistencia_w	:= cd_inconsistencia_w || substr(ds_pendencia_w,i,1);
	end if;
	end;
end loop;


return	substr(ds_retorno_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
