CREATE OR REPLACE FUNCTION obter_desc_perfil ( cd_perfil_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(40);
cd_exp_perfil_w		bigint;


BEGIN

	if (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') then

		select	max(ds_perfil),
			max(cd_exp_perfil)
		into STRICT	ds_retorno_w,
			cd_exp_perfil_w
		from	perfil
		where	cd_perfil = cd_perfil_p;

		if (cd_exp_perfil_w IS NOT NULL AND cd_exp_perfil_w::text <> '') then
			ds_retorno_w	:= coalesce(obter_desc_expressao(cd_exp_perfil_w),ds_retorno_w);
		end if;

	end if;

	return ds_retorno_w;

end;
$body$
