CREATE OR REPLACE FUNCTION obter_desc_pergunta_secreta (nr_sequencia_p integer) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);			


BEGIN

begin
select	max(obter_desc_expressao(cd_exp_descricao, ds_descricao)) ds_descricao
into STRICT	ds_retorno_w
from	pergunta_recup_senha
where	nr_sequencia = nr_sequencia_p;
exception
when no_data_found then
	ds_retorno_w := '';
end;
	
return	ds_retorno_w;

end;
$body$
