CREATE OR REPLACE FUNCTION obter_desc_perg_impedimento (nr_seq_pergunta_p bigint) RETURNS varchar AS $body$
DECLARE

ds_pergunta_w	varchar(255);

BEGIN
ds_pergunta_w := '';

if (nr_seq_pergunta_p <> 0) then
	select	ds_impedimento
	into STRICT	ds_pergunta_w
	from	san_impedimento
	where 	nr_sequencia = nr_seq_pergunta_p;

end if;

return	ds_pergunta_w;

end;
$body$
LANGUAGE PLPGSQL
