CREATE OR REPLACE FUNCTION obter_desc_pessoa_entrega ( nr_seq_pessoa_entrega_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);


BEGIN
ds_retorno_w		:= '';
if (nr_seq_pessoa_entrega_p IS NOT NULL AND nr_seq_pessoa_entrega_p::text <> '') then
	begin
	select	substr(max(ds_tipo_pessoa_entrega),1,80)
	into STRICT	ds_retorno_w
	from	cme_recebimento_terceiro
	where	nr_sequencia = nr_seq_pessoa_entrega_p;
	end;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
