CREATE OR REPLACE FUNCTION obter_desc_pflegegrad ( cd_pessoa_fisica_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pflegegrad_w			smallint;
ie_erfolgter_w			varchar(1);
ds_pflegegrad_w			varchar(4000);


BEGIN

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select  max(ie_pflegegrad)
        into STRICT    ie_pflegegrad_w
        from    paciente_pflegegrad
        where   cd_pessoa_fisica = cd_pessoa_fisica_p
        and    	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
        and    	coalesce(dt_inativacao::text, '') = ''
		and		(ie_pflegegrad IS NOT NULL AND ie_pflegegrad::text <> '')
        and    	dt_registro = (	SELECT 	max(dt_registro)
								from 	paciente_pflegegrad
								where 	cd_pessoa_fisica = cd_pessoa_fisica_p
								and    	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
								and 	coalesce(dt_inativacao::text, '') = ''
								and 	(ie_pflegegrad IS NOT NULL AND ie_pflegegrad::text <> ''));
	end if;

	if (coalesce(ie_pflegegrad_w,0) = 6) then
		select	coalesce(obter_desc_expressao(886693),'')
		into STRICT	ds_pflegegrad_w
		from	valor_dominio_v
		where	vl_dominio = ie_pflegegrad_w
		and		cd_dominio = 8732;
	elsif (ie_pflegegrad_w IS NOT NULL AND ie_pflegegrad_w::text <> '') then
		select	obter_valor_dominio(8732, ie_pflegegrad_w)
		into STRICT	ds_pflegegrad_w
		;
	end if;

	return ds_pflegegrad_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
