CREATE OR REPLACE FUNCTION obter_desc_plano_conv ( cd_convenio_p bigint, cd_plano_p text) RETURNS varchar AS $body$
DECLARE



ds_plano_w		varchar(80);


BEGIN
if (cd_convenio_p	is not  null) and (cd_plano_p IS NOT NULL AND cd_plano_p::text <> '') then
	select	max(ds_plano)
	into STRICT	ds_plano_w
	from	Convenio_Plano
	where	cd_convenio	= coalesce(cd_convenio_p,0)
	and	cd_plano	= coalesce(cd_plano_p,'0');
end if;

return	ds_plano_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
