CREATE OR REPLACE FUNCTION obter_desc_pontuacao_asg_ppp (qt_pontuacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);


BEGIN
if (qt_pontuacao_p < 2) then
	ds_retorno_w := 'Não é necessário intervenção nutricional neste momento. Reavaliação regular de rotina';
elsif (qt_pontuacao_p < 4) then
	ds_retorno_w := 'Educação ao doente/família por nutricionista, enfermeira ou outros, com intervenções farmacológicas, como indicado pela caixa 3 e valores laboratoriais apropriados';
elsif (qt_pontuacao_p < 9) then
	ds_retorno_w := 'Requer intervenção nutricional por nutricionista em conjugação com a enfermeira ou médico conforme indicado na caixa 3';
elsif (qt_pontuacao_p >= 9) then
	ds_retorno_w := 'Indica uma necessidade crítica para controle dos sintomas e/ou opções de intervenção nutricional';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
