CREATE OR REPLACE FUNCTION obter_desc_pontuacao_cook ( qt_pontuacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255):= '';


BEGIN

if (qt_pontuacao_p = 4) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309080); --'Atestesiado';
elsif (qt_pontuacao_p >= 5) and (qt_pontuacao_p <= 7) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309081); --'Sedação profunda';
elsif (qt_pontuacao_p >= 8) and (qt_pontuacao_p <= 11) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309082); --'Sedação moderada';
elsif (qt_pontuacao_p >= 12) and (qt_pontuacao_p <= 14) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309083); --'Sedação leve';
elsif (qt_pontuacao_p >= 15) and (qt_pontuacao_p <= 17) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309084); --'Dormindo';
elsif (qt_pontuacao_p > 17) and (qt_pontuacao_p <= 19) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309085); --'Acordado';
end if;

return	ds_retorno_w;

end;
$body$
