CREATE OR REPLACE FUNCTION obter_desc_pontuacao_mews (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_expressao_w 	varchar(255) := null;
qt_pontuacao_w	smallint;


BEGIN

	select  coalesce(qt_pontuacao,-1)
	into STRICT	qt_pontuacao_w
	from	escala_mews
	where	nr_sequencia = nr_sequencia_p;
	
	if (qt_pontuacao_w in (0,1,2)) then
		ds_expressao_w := obter_desc_expressao(1042535);
	elsif qt_pontuacao_w = 3 then
		ds_expressao_w := obter_desc_expressao(1042539);
	elsif qt_pontuacao_w = 4 then
		ds_expressao_w := obter_desc_expressao(1042537);
	elsif qt_pontuacao_w >= 5 then
		ds_expressao_w := obter_desc_expressao(1042541);
	end if;
	
	return ds_expressao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
