CREATE OR REPLACE FUNCTION obter_desc_pontuacao_mirels (qt_pontuacao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);

BEGIN

if (qt_pontuacao_p <= 7) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309087); --'Radioterapia e observação';
elsif (qt_pontuacao_p = 8) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309088); --'Decidir através de julgamento clínico';
elsif (qt_pontuacao_p >= 9) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309089); --'Fixação profilática';
end if;

return	ds_retorno_w;

end;
