CREATE OR REPLACE FUNCTION obter_desc_pont_icpp (qt_ponto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (qt_ponto_p between 11 and 17) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(1038891); -- 'Cuidados Mínimos';
elsif (qt_ponto_p between 18 and 23) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(1038893); -- 'Cuidados Intermediários';
elsif (qt_ponto_p between 24 and 30) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(1038894); -- 'Alta dependência';
elsif (qt_ponto_p between 31 and 36) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(1038895); -- 'Semi-Intensivos';
elsif (qt_ponto_p between 37 and 44) then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(1038896); -- 'Cuidados Intensivos';
end if;

return	ds_retorno_w;

end;
