CREATE OR REPLACE FUNCTION obter_desc_porte_cirurgico (ie_porte_cirurgico_p text) RETURNS varchar AS $body$
DECLARE


ds_porte_cirurgico_w	varchar(15);


BEGIN

if (ie_porte_cirurgico_p = 'P') then
	ds_porte_cirurgico_w := wheb_mensagem_pck.get_texto(485242); --'Pequeno';
elsif (ie_porte_cirurgico_p = 'M') then
	ds_porte_cirurgico_w := wheb_mensagem_pck.get_texto(485243); --'Médio';
elsif (ie_porte_cirurgico_p = 'G') then
	ds_porte_cirurgico_w := wheb_mensagem_pck.get_texto(485244); --'Grande';
elsif (ie_porte_cirurgico_p = 'NP') then
	ds_porte_cirurgico_w := wheb_mensagem_pck.get_texto(485246); --'Não Previsível';
end if;

return	ds_porte_cirurgico_w;

end;
