CREATE OR REPLACE FUNCTION obter_desc_precaucao_cih (NR_SEQ_CIH_P bigint) RETURNS varchar AS $body$
DECLARE


DS_RESULTADO_W			varchar(100);


BEGIN

IF (NR_SEQ_CIH_P IS NOT NULL AND NR_SEQ_CIH_P::text <> '') THEN
	SELECT	a.ds_precaucao
	INTO STRICT 	ds_resultado_w
	FROM	cih_precaucao a,
			atendimento_precaucao b
	WHERE	a.NR_SEQUENCIA = b.NR_SEQ_PRECAUCAO
	AND 	b.nr_sequencia = NR_SEQ_CIH_P;
END IF;

RETURN DS_RESULTADO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
