CREATE OR REPLACE FUNCTION obter_desc_precaucao_fim_isola (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
ds_precaucao_w		varchar(150);
nr_seq_precaucao_w	bigint;

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	select	max(a.nr_sequencia) 
	into STRICT	nr_seq_precaucao_w 
	from	atendimento_precaucao a, 
		atendimento_paciente b 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	 
	select	max(a.ds_precaucao) 
	into STRICT	ds_precaucao_w 
	from	cih_precaucao a, 
		atendimento_precaucao b 
	where	b.nr_seq_precaucao	= a.nr_sequencia 
	and	b.nr_sequencia		= nr_seq_precaucao_w;
 
end if;
 
return	ds_precaucao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
