CREATE OR REPLACE FUNCTION obter_desc_precaucao_sem_alta (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
ds_precaucao_w		varchar(150);
nr_seq_precaucao_w	bigint;

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	select	max(k.nr_sequencia) 
	into STRICT	nr_seq_precaucao_w 
	from	atendimento_precaucao k, 
		atendimento_paciente l 
	where	1 = 1 
	and	coalesce(k.dt_final_precaucao::text, '') = '' 
	and	k.nr_atendimento	= l.nr_atendimento 
	and	l.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	 
	select	max(n.ds_precaucao) 
	into STRICT	ds_precaucao_w 
	from	cih_precaucao n, 
		atendimento_precaucao k 
	where	1 = 1 
	and	k.nr_seq_precaucao	= n.nr_sequencia 
	and	k.nr_sequencia		= nr_seq_precaucao_w;
 
end if;
 
return	ds_precaucao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
