CREATE OR REPLACE FUNCTION obter_desc_prescr_proced ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_seq_exame_w		bigint;

ds_retorno_w		varchar(255);


BEGIN


select	coalesce(max(cd_procedimento),0),
	coalesce(max(ie_origem_proced),0),
	coalesce(max(nr_seq_exame),0)
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_exame_w
from prescr_procedimento
where nr_prescricao	= nr_prescricao_p
  and nr_sequencia	= nr_seq_prescr_p;

ds_retorno_w := '';

if (nr_seq_exame_w	<> 0) then
	select coalesce(max(nm_exame),'')
	into STRICT ds_retorno_w
	from exame_laboratorio
	where nr_seq_exame = nr_seq_exame_w;
elsif (cd_procedimento_w <> 0) then
	select coalesce(max(ds_procedimento),'')
	into STRICT ds_retorno_w
	from procedimento
	where cd_procedimento	= cd_procedimento_w
	  and ie_origem_proced	= ie_origem_proced_w;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
