CREATE OR REPLACE FUNCTION obter_desc_prescr_proc_autor (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, nr_sequencia_p bigint, ie_desc_nao_informado_p text default 'S') RETURNS varchar AS $body$
DECLARE


ds_procedimento_w	varchar(255);
nr_seq_interno_w	bigint;
nr_seq_proc_w		autorizacao_convenio.nr_seq_proc_interno%type;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	select	max(nr_seq_proc_interno)
	into STRICT	nr_seq_proc_w
	from	autorizacao_convenio
	where	nr_sequencia = nr_sequencia_p;
end if;

if (coalesce(nr_seq_proc_w,0) > 0)  then
	select	max(ds_proc_exame)
	into STRICT	ds_procedimento_w
	from	proc_interno
	where	nr_sequencia	= nr_seq_proc_w;
else
	select	obter_descricao_procedimento(cd_procedimento_p, ie_origem_proced_p)
	into STRICT	ds_procedimento_w
	;
end if;

if	(ie_desc_nao_informado_p = 'N') and (ds_procedimento_w = OBTER_DESC_EXPRESSAO(612231)) then -- 612231: 'NÃ£o encontrado'
	ds_procedimento_w := null;
end if;


return	ds_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
