CREATE OR REPLACE FUNCTION obter_desc_prescr_proc_exam (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ds_procedimento_w		varchar(255);
nr_seq_interno_w		bigint;

ie_ordem_converte_proc_w	varchar(2):= 'EP';
ie_converte_proc_conv_w		varchar(1);


BEGIN

begin
select	coalesce(ie_converte_proc_conv,'N'),
		coalesce(ie_ordem_converte_proc,'EP')
into STRICT	ie_converte_proc_conv_w,
	ie_ordem_converte_proc_w
from	parametro_faturamento
where	cd_estabelecimento	= wheb_usuario_pck.get_cd_estabelecimento;
exception
	when others then
		ie_converte_proc_conv_w:= 'N';
		ie_ordem_converte_proc_w:= 'EP';
end;

select	coalesce(max(nr_seq_proc_interno_p),0)
into STRICT	nr_seq_interno_w
;

if (ie_converte_proc_conv_w = 'S') and (coalesce(ie_ordem_converte_proc_w,'EP') = 'PE') then -- Proc. Interno / Exame de laboratÃ³rio
	if (coalesce(nr_seq_exame_p,0) > 0) then

		select	NM_EXAME
		into STRICT	ds_procedimento_w
		from	exame_laboratorio
		where	NR_SEQ_EXAME	= nr_seq_exame_p;

	end if;

	if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') and (coalesce(nr_seq_interno_w,0) > 0) then

		select	ds_proc_exame
		into STRICT	ds_procedimento_w
		from	proc_interno
		where	nr_sequencia	= nr_seq_interno_w;

	end if;
else
	if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') and (coalesce(nr_seq_interno_w,0) > 0) then
		select	ds_proc_exame
		into STRICT	ds_procedimento_w
		from	proc_interno
		where	nr_sequencia	= nr_seq_interno_w;
	end if;

	if (coalesce(nr_seq_exame_p,0) > 0) then
		select	NM_EXAME
		into STRICT	ds_procedimento_w
		from	exame_laboratorio
		where	NR_SEQ_EXAME	= nr_seq_exame_p;
	end if;

end if;

if (coalesce(ds_procedimento_w::text, '') = '') then
	select	obter_descricao_procedimento(cd_procedimento_p, ie_origem_proced_p)
	into STRICT	ds_procedimento_w
	;
end if;

return	ds_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
