CREATE OR REPLACE FUNCTION obter_desc_prestador_convenio ( cd_prestador_p text, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_prestador_p	varchar(200);

 

BEGIN 
 
select	substr(obter_nome_pf(cd_pessoa_fisica),1,200) 
into STRICT	ds_prestador_p 
from	medico_convenio 
where	cd_medico_convenio = to_char(cd_prestador_p) 
and	cd_convenio_p = cd_convenio 

union
 
SELECT	substr(obter_dados_pf_pj(NULL,cd_cgc,'F'),1,200) 
from	convenio_prestador 
where	cd_prestador_convenio 	= to_char(cd_prestador_p) 
and	cd_convenio		= cd_convenio_p;
	 
 
	 
return	ds_prestador_p;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
